import React, { useState } from 'react';
import AuthForm from '../components/AuthForm';
import { signup } from '../services/authService';

const Signup = () => {
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [userType, setUserType] = useState('singer'); // ברירת מחדל - זמר

  const handleSignupSubmit = async (formData) => {
    setError('');
    setSuccess(false);

    try {
      const userData = {
        ...formData,
        userType, // שליחת סוג המשתמש
      };

      if (userType === 'musician' && !formData.instrument) {
        setError('Please enter your instrument.');
        return;
      }

      const response = await signup(userData);
      console.log('Signup successful:', response);
      setSuccess(true);
    } catch (err) {
      console.error('Signup error:', err);
      setError(err.message || 'Something went wrong. Please try again.');
    }
  };

  return (
    <>
<AuthForm
  title="SIGN UP"
  subtitle="Create your account to get started"
  fields={[
    { name: 'name', type: 'text', placeholder: 'Enter your name', required: true },
    { name: 'email', type: 'email', placeholder: 'Enter your email', required: true },
    { name: 'password', type: 'password', placeholder: 'Enter your password', required: true },
    { name: 'userType', type: 'select', placeholder: 'Select user type', required: true, options: ['singer', 'musician'] } // 🎵 הוספת רשימה נפתחת
  ]}
  buttonText="Sign Up"
  onSubmit={handleSignupSubmit}
  additionalLink={{
    text: 'Already have an account?',
    linkText: 'Login',
    href: '/',
  }}
/>



      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
      {success && <p style={{ color: 'green', textAlign: 'center' }}>Signup successful! Redirecting...</p>}
    </>
  );
};

export default Signup;
